import React from 'react';
import Moment from 'moment';
import styled from 'styled-components';
import { getMembers, getMessages } from '../data';

const formatMessages = () => {
  return getMessages();
};

const formatMembers = () => {
  return getMembers();
};

const DisplayMessages = async () => {
  const messages = await formatMessages();
  const members = await formatMembers();

  const getUserEmail = (id) => {
    return members.find(m => m.id === id).email;
  };

  const getUserAvatar = (id) => {
    return members.find(m => m.id === id).avatar;
  };

  const getUserName = (id) => {
    const user = members.find(m => m.id === id);
    return `${user.firstName} ${user.lastName}`;
  }

  const getTimestamp = (id) => {
    return Moment(messages.find(m => m.id === id).timestamp).format('ddd mmm yyyy hh:mm');
  }

  const sortMessages = () => {
    return messages.sort((a, b) => a.timestamp - b.timestamp);
  };

  sortMessages();

  return (
    <>
      {messages.map(m => (
        <MessageSection>
          <MessageArticle>
            <Avatar alt={`Avatar for ${getUserName(m.userId)}`} src={getUserAvatar(m.userId)} />
            <Message title={getUserEmail(m.userId)}>
              <span>{m.message}</span>
              <span>{getTimestamp(m.id)}</span>
            </Message>
          </MessageArticle>
        </MessageSection>
      ))}
    </>
  );
};

const MessageSection = styled.section`
  min-height: 25px;
  width: 90%;
  margin: 0 5%;
  background-color: green;
`;

const MessageArticle = styled.article`
  border: 1px solid black;
`;

const Avatar = styled.img`
  width: 25px;
  height: 50px;
`;

const Message = styled.p`
  display: flex;
  flex: 0 1 auto;
`;

export default DisplayMessages;
