import React, { useEffect, useState } from 'react';
import Moment from 'moment';
import styled from 'styled-components';
import { getMembers, getMessages } from '../data';

const formatMessages = async () => {
  const m = await getMessages();
  console.log('messages', m);
  return m;
};

const formatMembers = async () => {
  const m = await getMembers();
  console.log('members', m);
  return m;
};

const DisplayMessages = () => {
  useEffect(() => {
    console.log('[] useEffect');
    const fetchMessages = async () => {
      const response = await formatMessages();
      console.log('messages', response);
    };

    const fetchMembers = async () => {
      const response = await formatMembers();
      console.log('members', response);
    };

    fetchMessages();
    fetchMembers();
  }, []);

  const getUserEmail = (id) => {
    return members.find(m => m.id === id).email;
  };

  const getUserAvatar = (id) => {
    return members.find(m => m.id === id).avatar;
  };

  const getUserName = (id) => {
    const user = members.find(m => m.id === id);
    return `${user.firstName} ${user.lastName}`;
  }

  const getTimestamp = (id) => {
    return Moment(messages.find(m => m.id === id).timestamp).format('ddd mmm yyyy hh:mm');
  }

  const sortMessages = () => {
    return messages.length > 1 ? messages.sort((a, b) => a.timestamp - b.timestamp) : [];
  };

  sortMessages();
  console.log('after sortMessages() messages is', messages);

  return (
    <>
      {messages.length > 1 && messages.map(m => (
        <MessageSection key={m.id}>
          <MessageArticle>
            <Avatar alt={`Avatar for ${getUserName(m.userId)}`} src={getUserAvatar(m.userId)} />
            <Message title={getUserEmail(m.userId)}>
              <span>{m.message}</span>
              <span>{getTimestamp(m.id)}</span>
            </Message>
          </MessageArticle>
        </MessageSection>
      ))}
    </>
  );
};

const MessageSection = styled.section`
  min-height: 25px;
  width: 90%;
  margin: 0 5%;
  background-color: green;
`;

const MessageArticle = styled.article`
  border: 1px solid black;
`;

const Avatar = styled.img`
  width: 25px;
  height: 50px;
`;

const Message = styled.p`
  display: flex;
  flex: 0 1 auto;

  span:nth-child(1) {
    flex: 0 1 25%;
  }
  span:nth-child(2) {
    flex: 0 1 75%;
  }
`;

export default DisplayMessages;
