import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import Moment from 'moment';
import styled from 'styled-components';
import { getMembers, getMessages } from '../data';

const formatMessages = async () => await getMessages();

const formatMembers = async () => await getMembers();

const DisplayUser = (userId) => {
  const location = useLocation();
  const [messages, setMessages] = useState([]);
  const [members, setMembers] = useState([]);
  console.log('location', location);

  useEffect(() => {
    const fetchMessages = async () => {
      const response = await formatMessages();
      setMessages(response);
    };

    const fetchMembers = async () => {
      const response = await formatMembers();
      const user = response.find(r => r.id === location.state.user);
      setMembers(user);
    };

    fetchMessages();
    fetchMembers();
  }, []);

  const sortMessages = () => {
    return messages.sort((a, b) => Moment(b.timestamp).unix() - Moment(a.timestamp).unix());
  };

  return (
    <>
      {messages.length > 1 && sortMessages(messages).map(m => (
        <MessageSection>
          <MessageArticle>
            <Message>
              <MessageText>{m.message}</MessageText>
              <MessageTime>{Moment(m.timestamp)}</MessageTime>
            </Message>
          </MessageArticle>
        </MessageSection>
      ))}
    </>
  );
};

const MessageSection = styled.section`
  background-color: #ffffee;
  min-height: 25px;
  margin: 0 5% 5px;
  width: 90%;
`;

const MessageArticle = styled.article`
  border: 1px solid #cccccc;
  display: flex;
  flex-end: justify-content;
  margin: 0;
`;

const Avatar = styled.img`
  flex: 0 1 100px;
  height: 100px;
  width: 100px;
`;

const Message = styled.p`
  align-content: flex-start;
  align-items: flex-start;
  flex: 0 1 100%;
`;

const MessageText = styled.span`
  display: flex;
  flex: 0 1 100%;
  margin: 0;
  padding: 0 0 0 10px;
`;

const MessageTime = styled.span`
  display: flex;
  flex: 0 1 100%;
  padding: 0 0 0 10px;
`;

export default DisplayUser;
