import React from 'react';
import Moment from 'moment';
import 'styled-components';
import { getMembers, getMessages } from '../data';

const formatMessages = () => {
  return getMessages();
};

const formatMembers = () => {
  return getMembers();
};

const DisplayMessages = () => {
  const messages = formatMessages();
  const members = formatMembers();

  const getUserEmail = (id) => {
    return members.find(m => m.id === id).email;
  };

  const getUserAvatar = (id) => {
    return members.find(m => m.id === id).avatar;
  };

  const getUserName = (id) => {
    const user = members.find(m => m.id === id);
    return `${user.firstName} ${user.lastName}`;
  }

  const getTimestamp = (id) => {
    return Moment(messages.find(m => m.id === id).timestamp).format('ddd mmm yyyy hh:mm');
  }

  const sortMessages = (a) => {
    a.sort((a, b) => a.timestamp - b.timestamp);
  };

  sortMessages(messages);
  console.log('messages', messages);

  return (
    messages.map(m => {
      return (
        <section>
          <article>
            <img alt={`Avatar for ${getUserName()}`} src={getUserAvatar(m.userId)} />
            <p title={getUserEmail(m.userId)}>
              <span>{m.message}</span>
              <span>{getTimestamp(m.id)}</span>
            </p>
          </article>
        </section>
      )
    })
  );
};

export default DisplayMessages;
